import { Component, OnDestroy, OnInit } from '@angular/core'
import { Subscription } from 'rxjs'
import { Router, NavigationEnd } from "@angular/router";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit, OnDestroy {
  isShowNav = true;
  isMiniSidebar = false
  classes = 'main'
  clickMenuIconSubscription!: Subscription;

  constructor(private router : Router) {
  }
  ngOnInit(): void {
    this.router.events.subscribe(event => {
      if(event instanceof NavigationEnd){
        this.isShowNav = event.url !== '/auth';
      }
    })
  }

  ngOnDestroy() {
    this.clickMenuIconSubscription.unsubscribe();
  }

  onClickMenuIcon() {
    this.isMiniSidebar = !this.isMiniSidebar
    this.classes = this.isMiniSidebar ? 'main mini-sidebar' : 'main'
  }

  onClickOverlay() {
    this.isMiniSidebar = true
    this.classes = 'main mini-sidebar'
  }
}
<div class="{{ classes }}" *ngIf="isShowNav; else loginTemplate">
  <!-- Header -->
  <app-header></app-header>

  <!-- Sidebar -->
  <app-sidebar></app-sidebar>

  <!-- Overlay -->
  <div

    class="fixed top-header right-0 bottom-0 left-0 bg-black/40 lg:hidden cursor-pointer"
    [ngClass]="{ hidden: isMiniSidebar }"
    (click)="onClickOverlay()"
  ></div>

  <!-- Content -->
  <div class="main-content">
    <router-outlet></router-outlet>
  </div>
</div>
<ng-template #loginTemplate>
  <div class="bg-content">
    <router-outlet></router-outlet>
  </div>
</ng-template>
